// --- H├АM Xс╗м L├Э Dс╗КCH THUс║мT ---

/**
 * ├Бp dс╗еng bс║гn dс╗Лch cho to├аn bс╗Щ trang web dс╗▒a tr├кn cс║еu tr├║c DOM v├а chс╗Й sс╗С.
 * @param {string} langCode - M├г ng├┤n ngс╗п (ja, en, vi, ko, ne).
 */
function translatePage(langCode) {
    // 1. Thay ─Сс╗Хi thuс╗Щc t├нnh lang cс╗зa HTML
    document.documentElement.lang = langCode;

    // 2. Dс╗Лch ti├кu ─Сс╗Б t├аi liс╗Зu
    document.title = translations.title[langCode];

    // 3. Dс╗Лch Nav Brand
    const navBrandElement = document.querySelector('.navbar-brand');
    if (navBrandElement) {
        navBrandElement.textContent = translations.navBrand[langCode];
    }

    // 4. Dс╗Лch Nav Links
    const navLinks = document.querySelectorAll('#navbarNav .navbar-nav a.nav-link');
    translations.navLinks.forEach((t, index) => {
        if (navLinks[index]) {
            navLinks[index].textContent = t[langCode];
        }
    });

    // 5. Dс╗Лch Hero Title
    const heroTitleElement = document.querySelector('.header h1');
    if (heroTitleElement) {
        heroTitleElement.textContent = translations.heroTitle[langCode];
    }

    // 6. Dс╗Лch About Section
    const aboutSection = document.getElementById('about');
    if (aboutSection) {
        // Heading
        aboutSection.querySelector('.section-title').textContent = translations.about.heading[langCode];
        
        // ─Рoс║бn v─Гn P chс╗йa quote
        const pElement = aboutSection.querySelector('.card-text.text');
        
        if (pElement) {
            // Dс╗Лch nс╗Щi dung P1
            // ChildNode 0 l├а text node chс╗йa P1
            // Cс║зn ─Сс║гm bс║гo giс╗п nguy├кn khoс║гng trс║пng hoс║╖c th├кm v├аo ─Сс╗Г bс╗С cс╗еc kh├┤ng bс╗Л x├┤ lс╗Зch nhiс╗Бu
            pElement.childNodes[0].nodeValue = translations.about.p1[langCode].trim() + '\n\t\t\t\t\t\t\t\t\t\t\t\t';

            // Dс╗Лch Quote (thс║╗ p.eyes)
            pElement.querySelector('.eyes').textContent = translations.about.quote[langCode];

            // Dс╗Лch nс╗Щi dung P2
            // ChildNode cuс╗Сi c├╣ng l├а text node chс╗йa P2
            pElement.childNodes[pElement.childNodes.length - 1].nodeValue = '\n\t\t\t\t\t\t\t\t\t\t\t\t' + translations.about.p2[langCode].trim() + '\n\t\t\t\t\t\t\t';
        }
    }
    
    // 7. Dс╗Лch Spots Section
    const spotsSection = document.getElementById('spots');
    if (spotsSection) {
        // Section Title
        spotsSection.querySelector('.section-title').textContent = translations.spotsHeading[langCode];

        // Dс╗Лch tс╗лng Card
        const cardElements = spotsSection.querySelectorAll('.card');
        cardElements.forEach((card, cardIndex) => {
            const cardData = translations.cards[cardIndex];
            if (!cardData) return;

            // Title
            card.querySelector('.card-title').textContent = cardData.title[langCode];

            // Paragraph (Sс╗н dс╗еng innerHTML ─Сс╗Г giс╗п thс║╗ <br>)
            card.querySelector('.card-text.text').innerHTML = cardData.p[langCode]; 

            // Table
            const tdElements = card.querySelectorAll('td');
            const tableData = cardData.table; 
            
            // Dс╗Лch ti├кu ─Сс╗Б bс║гng (Open, Closed, Fee)
            // Lс║еy thс║╗ <b> b├кn trong td[0], td[2], td[4]
            if(tdElements[0] && tableData[0]) tdElements[0].querySelector('b').textContent = tableData[0][langCode];
            if(tdElements[2] && tableData[2]) tdElements[2].querySelector('b').textContent = tableData[2][langCode];
            if(tdElements[4] && tableData[4]) tdElements[4].querySelector('b').textContent = tableData[4][langCode];

            // Dс╗Лch nс╗Щi dung bс║гng
            if(tdElements[1] && tableData[1]) tdElements[1].textContent = tableData[1][langCode];
            if(tdElements[3] && tableData[3]) tdElements[3].textContent = tableData[3][langCode];
            if(tdElements[5] && tableData[5]) tdElements[5].innerHTML = tableData[5][langCode]; // D├╣ng innerHTML cho nс╗Щi dung ph├н ─Сс╗Г giс╗п thс║╗ <b>
        });
    }

    // 8. Dс╗Лch Map Title
    const mapTitleElement = document.querySelector('#map .map_title');
    if (mapTitleElement) {
        mapTitleElement.textContent = translations.mapTitle[langCode];
    }

    // 9. Dс╗Лch Footer
    const footerElement = document.querySelector('.footer p');
    if (footerElement) {
        footerElement.textContent = translations.footer[langCode];
    }

    // 10. Cс║нp nhс║нt nh├гn chс╗Нn ng├┤n ngс╗п trong dropdown
    const langLabel = document.getElementById('langDropdown');
    const langMap = {
        ja: 'ЁЯЗпЁЯЗ╡ цЧецЬмшкЮ (JA)',
        en: 'ЁЯЗмЁЯЗз English (EN)',
        vi: 'ЁЯЗ╗ЁЯЗ│ Tiс║┐ng Viс╗Зt (VI)',
        ko: 'ЁЯЗ░ЁЯЗ╖ эХЬъ╡ньЦ┤ (KO)',
        ne: 'ЁЯЗ│ЁЯЗ╡ рдиреЗрдкрд╛рд▓реА (NE)',
    };
    if (langLabel) {
        langLabel.innerHTML = `ЁЯМР ${langMap[langCode] || langCode.toUpperCase()}`;
    }
}


/**
 * Thiс║┐t lс║нp tr├мnh nghe sс╗▒ kiс╗Зn cho c├бc li├кn kс║┐t chс╗Нn ng├┤n ngс╗п.
 */
function setupLanguageSwitcher() {
    const langLinks = document.querySelectorAll('#langDropdown + .dropdown-menu a.dropdown-item');
    langLinks.forEach(link => {
        // Lс║еy m├г ng├┤n ngс╗п tс╗л href (v├н dс╗е: kurashiki-en.html -> en)
        const href = link.getAttribute('href');
        const match = href.match(/kurashiki-(ja|en|vi|ko|ne)\.html/);
        
        if (match) {
            const langCode = match[1];

            // Th├кm tr├мnh nghe sс╗▒ kiс╗Зn
            link.addEventListener('click', function(event) {
                event.preventDefault(); // Ng─Гn chс║╖n viс╗Зc chuyс╗Гn trang
                
                // L╞░u ng├┤n ngс╗п ─С├г chс╗Нn v├аo localStorage
                localStorage.setItem('selectedLang', langCode);
                
                // ├Бp dс╗еng dс╗Лch thuс║нt
                translatePage(langCode);
            });
        }
    });
}

// --- CHс╗иC N─ВNG ─РIс╗АU H╞пс╗ЪNG Bс║░NG SCROLL/SMOOTH SCROLL (Nguy├кn gс╗Сc tс╗л test.js) ---

function setupSmoothScroll() {
    const navLinks = document.querySelectorAll('a.nav-link[href^="#"]');
    navLinks.forEach(link => {
        link.addEventListener('click', function(event) {
            // Lс║еy ID mс╗еc ti├кu
            const targetId = this.getAttribute('href').substring(1);
            const targetElement = document.getElementById(targetId);

            if (targetElement) {
                event.preventDefault(); // Ng─Гn chс║╖n h├аnh vi nhс║гy trang mс║╖c ─Сс╗Лnh
                
                // Cuс╗Щn m╞░с╗гt m├а ─Сс║┐n phс║зn tс╗н
                targetElement.scrollIntoView({
                    behavior: 'smooth'
                });
            }
        });
    });
}

// --- KHс╗ЮI Tс║аO Tс║дT Cс║в CHс╗иC N─ВNG ---

document.addEventListener('DOMContentLoaded', () => {
    // 1. Thiс║┐t lс║нp cuс╗Щn trang m╞░с╗гt m├а
    setupSmoothScroll();

    // 2. Thiс║┐t lс║нp chuyс╗Гn ─Сс╗Хi ng├┤n ngс╗п
    setupLanguageSwitcher();

    // 3. Kiс╗Гm tra ng├┤n ngс╗п ─С├г l╞░u hoс║╖c mс║╖c ─Сс╗Лnh (Nhс║нt Bс║гn l├а mс║╖c ─Сс╗Лnh)
    let initialLang = localStorage.getItem('selectedLang') || 'ja';
    
    // ├Бp dс╗еng dс╗Лch thuс║нt lс║зn ─Сс║зu
    translatePage(initialLang);
});
